import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useNavigate } from 'react-router-dom';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

function LoginPage({ setIsLoggedIn }) {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (username && password) {
      setIsLoggedIn(true);
      navigate('/my-page');
    } else {
      alert('Please enter both username and password');
    }
  };

  return (
    <div style={styles.loginContainer}>
      <h2>Login Page</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={styles.input}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={styles.input}
      />
      <button onClick={handleLogin} style={styles.button}>Login</button>
    </div>
  );
}

function MyPage({ isLoggedIn }) {
  if (!isLoggedIn) {
    return <Navigate to="/" />;
  }

  return (
    <div style={styles.myPageContainer}>
      <h2>My Page</h2>
      <p>Welcome to your personal dashboard!</p>
      <Link to="/bitcoin-tracker" style={styles.link}>Go to Bitcoin Tracker</Link>
    </div>
  );
}

function BitcoinTracker({ isLoggedIn }) {
  if (!isLoggedIn) {
    return <Navigate to="/" />;
  }

  const [priceData, setPriceData] = useState([]);
  const [timeData, setTimeData] = useState([]);
  const [volumeData, setVolumeData] = useState([]);
  const [tradeHistory, setTradeHistory] = useState([]);
  const [trend, setTrend] = useState('상승세');
  const [totalProfit, setTotalProfit] = useState(5000);
  const [previousProfit, setPreviousProfit] = useState(5000);
  const [profitChange, setProfitChange] = useState(0);
  const [isProfitIncreasing, setIsProfitIncreasing] = useState(true);
  const [accuracy, setAccuracy] = useState(85);
  const [profitRate, setProfitRate] = useState(20);

  useEffect(() => {
    const fetchDummyData = () => {
      const dummyPrice = (Math.random() * (60000 - 50000) + 50000).toFixed(2);
      const dummyVolume = (Math.random() * 100).toFixed(2);
      const currentTime = new Date().toLocaleTimeString();

      setProfitRate(Math.floor(Math.random() * 100));
      setAccuracy(Math.floor(Math.random() * 100));

      const newProfit = totalProfit + Math.floor(Math.random() * 200 - 100);
      setPreviousProfit(totalProfit);
      setTotalProfit(newProfit);

      const change = newProfit - previousProfit;
      setProfitChange(change);
      setIsProfitIncreasing(change >= 0);

      setPriceData((prevPriceData) => [...prevPriceData, dummyPrice]);
      setTimeData((prevTimeData) => [...prevTimeData, currentTime]);
      setVolumeData((prevVolumeData) => [...prevVolumeData, dummyVolume]);

      const newTrade = {
        time: currentTime,
        price: dummyPrice,
        volume: dummyVolume,
        action: Math.random() > 0.5 ? 'Buy' : 'Sell',
        result: Math.random() > 0.5 ? 'Win' : 'Lose',
      };
      setTradeHistory((prevHistory) => {
        const updatedHistory = [...prevHistory, newTrade];
        return updatedHistory.length > 15 ? updatedHistory.slice(-15) : updatedHistory;
      });

      const recentPrices = [...priceData, dummyPrice].slice(-5);
      const priceDifference = recentPrices[recentPrices.length - 1] - recentPrices[0];
      setTrend(priceDifference > 0 ? '상승세' : '하락세');
    };

    const interval = setInterval(fetchDummyData, 300);
    return () => clearInterval(interval);
  }, [priceData, totalProfit, previousProfit]);

  return (
    <div>
      <div style={styles.header}>
        <h1>Bitcoin Tracker with Dummy Data</h1>
        <div style={styles.totalProfit}>
          <h2 style={styles.fixedProfit}>
            Total Profit: ${totalProfit.toLocaleString()}
            <span style={{ color: isProfitIncreasing ? 'green' : 'red' }}>
              {isProfitIncreasing ? ` (+${profitChange}) ↑` : ` (${profitChange}) ↓`}
            </span>
          </h2>
        </div>
      </div>

      {/* Chart and table code here */}
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage setIsLoggedIn={setIsLoggedIn} />} />
        <Route path="/bitcoin-tracker" element={<BitcoinTracker isLoggedIn={isLoggedIn} />} />
        <Route path="/my-page" element={<MyPage isLoggedIn={isLoggedIn} />} />
      </Routes>
    </Router>
  );
}

const styles = {
  loginContainer: { display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '100px' },
  input: { margin: '10px', padding: '10px', fontSize: '16px', width: '200px' },
  button: { padding: '10px 20px', fontSize: '16px', cursor: 'pointer' },
  myPageContainer: { textAlign: 'center', marginTop: '50px' },
  link: { textDecoration: 'none', color: 'blue', fontSize: '18px', marginTop: '20px', display: 'block' },
  header: { display: 'flex', justifyContent: 'space-between', alignItems: 'center', paddingBottom: '20px', borderBottom: '2px solid #ccc' },
};

export default App;
