import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

function LoginPage({ setIsLoggedIn, setUsername }) {
  const navigate = useNavigate();
  const [inputUsername, setInputUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (inputUsername && password) {
      setUsername(inputUsername);
      setIsLoggedIn(true);
      navigate('/dashboard');
    } else {
      alert('Please enter both username and password');
    }
  };

  return (
    <div style={styles.loginContainer}>
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={inputUsername}
        onChange={(e) => setInputUsername(e.target.value)}
        style={styles.input}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={styles.input}
      />
      <button onClick={handleLogin} style={styles.button}>Log In</button>
    </div>
  );
}

function BitcoinTracker({ isLoggedIn, username }) {
  const [priceData, setPriceData] = useState([]);
  const [timeData, setTimeData] = useState([]);
  const [volumeData, setVolumeData] = useState([]);
  const [tradeHistory, setTradeHistory] = useState([]);
  const [cumulativeReturn, setCumulativeReturn] = useState(100);
  const [winRate, setWinRate] = useState(0);
  const [averageHoldingTime, setAverageHoldingTime] = useState(0);
  const [riskRewardRatio, setRiskRewardRatio] = useState(0);

  useEffect(() => {
    const fetchDummyData = () => {
      const dummyPrice = (Math.random() * (60000 - 50000) + 50000).toFixed(2);
      const dummyVolume = (Math.random() * 100).toFixed(2);
      const currentTime = new Date().toLocaleTimeString();
      const newReturn = cumulativeReturn + Math.random() * 2 - 1;
      setCumulativeReturn(newReturn);

      const isWin = Math.random() > 0.5;
      setTradeHistory((prevHistory) => [
        ...prevHistory,
        { time: currentTime, isWin, holdingTime: Math.floor(Math.random() * 60) },
      ]);

      const winCount = tradeHistory.filter((trade) => trade.isWin).length;
      setWinRate((winCount / tradeHistory.length) * 100);

      const totalHoldingTime = tradeHistory.reduce((acc, trade) => acc + trade.holdingTime, 0);
      setAverageHoldingTime(totalHoldingTime / tradeHistory.length || 0);

      const totalProfit = tradeHistory.reduce((acc, trade) => acc + (trade.isWin ? 1 : -1), 0);
      const totalLoss = tradeHistory.length - winCount;
      setRiskRewardRatio((totalProfit / totalLoss) || 0);

      setPriceData((prevPriceData) => [...prevPriceData, dummyPrice]);
      setTimeData((prevTimeData) => [...prevTimeData, currentTime]);
      setVolumeData((prevVolumeData) => [...prevVolumeData, dummyVolume]);
    };

    const interval = setInterval(fetchDummyData, 1000);
    return () => clearInterval(interval);
  }, [tradeHistory, cumulativeReturn]);

  if (!isLoggedIn) {
    return <Navigate to="/" />;
  }

  const priceChartData = {
    labels: timeData,
    datasets: [
      {
        label: 'Bitcoin Price',
        data: priceData,
        fill: false,
        borderColor: 'blue',
        tension: 0.4,
        borderWidth: 1,
        pointRadius: 0,
      },
    ],
  };

  const cumulativeReturnData = {
    labels: timeData,
    datasets: [
      {
        label: 'Cumulative Return',
        data: tradeHistory.map((_, index) => cumulativeReturn + index * (Math.random() - 0.5) * 2),
        fill: true,
        borderColor: 'purple',
        backgroundColor: 'rgba(128, 0, 128, 0.3)',
        borderWidth: 2,
      },
    ],
  };

  return (
    <div>
      <div style={styles.header}>
        <h1>{username}님 안녕하세요</h1>
      </div>

      <div style={styles.container}>
        <div style={styles.column}>
          <h2>Bitcoin Price</h2>
          <Line data={priceChartData} options={{ scales: { x: { ticks: { maxTicksLimit: 10 } } } }} />

          <h2>Cumulative Return</h2>
          <Line data={cumulativeReturnData} options={{ scales: { x: { ticks: { maxTicksLimit: 10 } } } }} />
        </div>

        <div style={styles.column}>
          <h2>Trade Metrics</h2>
          <p>Win Rate: {winRate.toFixed(2)}%</p>
          <p>Average Holding Time: {averageHoldingTime.toFixed(2)} minutes</p>
          <p>Risk-Reward Ratio: {riskRewardRatio.toFixed(2)}</p>
        </div>
      </div>
    </div>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />} />
        <Route path="/dashboard" element={<BitcoinTracker isLoggedIn={isLoggedIn} username={username} />} />
      </Routes>
    </Router>
  );
}

const styles = {
  loginContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    backgroundColor: '#f0f2f5',
  },
  input: {
    margin: '10px',
    padding: '12px',
    fontSize: '16px',
    width: '250px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    cursor: 'pointer',
    backgroundColor: '#4caf50',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    borderBottom: '2px solid #ccc',
    backgroundColor: '#f0f2f5',
  },
  container: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    flexWrap: 'wrap',
  },
  column: {
    flex: 1,
    minWidth: '300px',
    marginRight: '10px',
    marginBottom: '20px',
  },
};

export default App;
