import React, { useState, useEffect } from 'react';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

function App() {
  const [priceData, setPriceData] = useState([]);
  const [timeData, setTimeData] = useState([]);
  const [volumeData, setVolumeData] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [tradeHistory, setTradeHistory] = useState([]);
  const [trend, setTrend] = useState('상승세'); // 초기값은 상승세로 설정
  const [totalProfit, setTotalProfit] = useState(5000); // 총수익 초기값
  const [accuracy, setAccuracy] = useState(85); // 예측 정확도 초기값
  const [profitRate, setProfitRate] = useState(20); // 수익률 초기값

  useEffect(() => {
    const fetchDummyData = () => {
      // 더미 데이터 업데이트
      const dummyPrice = (Math.random() * (60000 - 50000) + 50000).toFixed(2);
      const dummyVolume = (Math.random() * 100).toFixed(2);
      const currentTime = new Date().toLocaleTimeString();

      // 실시간으로 수익률과 정확도 변경
      setProfitRate(Math.floor(Math.random() * 100)); // 0~100% 사이의 랜덤 수익률
      setAccuracy(Math.floor(Math.random() * 100)); // 0~100% 사이의 랜덤 정확도

      setPriceData((prevPriceData) => [...prevPriceData, dummyPrice]);
      setTimeData((prevTimeData) => [...prevTimeData, currentTime]);
      setVolumeData((prevVolumeData) => [...prevVolumeData, dummyVolume]);

      const recentPrices = [...priceData, dummyPrice].slice(-5);
      const priceDifference = recentPrices[recentPrices.length - 1] - recentPrices[0];
      setTrend(priceDifference > 0 ? '상승세' : '하락세');
    };

    fetchDummyData();
    const interval = setInterval(fetchDummyData, 2000);
    return () => clearInterval(interval);
  }, [priceData]);

  const priceChartData = {
    labels: timeData,
    datasets: [
      {
        label: 'Bitcoin Price',
        data: priceData,
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  };

  const volumeChartData = {
    labels: timeData,
    datasets: [
      {
        label: 'Bitcoin Volume',
        data: volumeData,
        backgroundColor: 'green',
      },
    ],
  };

  const profitRateData = {
    labels: ['Profit', 'Loss'],
    datasets: [
      {
        label: 'Profit Rate',
        data: [profitRate, 100 - profitRate], // 수익률 및 손실 비율
        backgroundColor: ['#4caf50', '#f44336'],
        hoverOffset: 4,
      },
    ],
  };

  const accuracyData = {
    labels: ['Accuracy', 'Error'],
    datasets: [
      {
        label: 'Prediction Accuracy',
        data: [accuracy, 100 - accuracy], // 정확도 및 오류 비율
        backgroundColor: ['#2196f3', '#ff9800'],
        hoverOffset: 4,
      },
    ],
  };

  return (
    <div>
      <h1>Bitcoin Tracker with Dummy Data</h1>

      <div style={styles.container}>
        <div style={styles.column}>
          <h2>Bitcoin Price</h2>
          <Line data={priceChartData} />

          <h2>Bitcoin Volume</h2>
          <Bar data={volumeChartData} />
        </div>

        <div style={styles.column}>
          <h2>Trade History (Dummy) - Latest 17</h2>
          <table border="1" cellPadding="10" cellSpacing="0">
            <thead>
              <tr>
                <th>Time</th>
                <th>Price (USD)</th>
                <th>Volume</th>
              </tr>
            </thead>
            <tbody>
              {tradeHistory.map((trade, index) => (
                <tr key={index}>
                  <td>{trade.time}</td>
                  <td>{trade.price}</td>
                  <td>{trade.volume}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div style={styles.column}>
          <h2>Current Trend: {trend}</h2>
          <p>최근 5개의 가격을 기반으로 분석된 결과: {trend}</p>

          <h2>Profit Rate</h2>
          <div style={styles.chartWrapper}>
            <Doughnut data={profitRateData} />
            <p style={styles.chartText}>{profitRate}%</p> {/* 실시간 수익률 표시 */}
          </div>

          <h2>Prediction Accuracy</h2>
          <div style={styles.chartWrapper}>
            <Doughnut data={accuracyData} />
            <p style={styles.chartText}>{accuracy}%</p> {/* 실시간 정확도 표시 */}
          </div>

          <h2>Total Profit</h2>
          <p>${totalProfit}</p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    flexWrap: 'wrap',
  },
  column: {
    flex: 1,
    minWidth: '300px',
    marginRight: '10px',
    marginBottom: '20px',
  },
  chartWrapper: {
    position: 'relative',
    width: '300px',  // 도넛 차트 크기를 더 크게 설정
    height: '300px',
    margin: '0 auto',
  },
  chartText: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)', // 중앙에 텍스트 배치
    fontSize: '24px',
    fontWeight: 'bold',
  },
};

export default App;
