import React, { useState, useEffect } from 'react';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  BarElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend);

function App() {
  const [priceData, setPriceData] = useState([]);
  const [timeData, setTimeData] = useState([]);
  const [volumeData, setVolumeData] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [tradeHistory, setTradeHistory] = useState([]);
  const [trend, setTrend] = useState('상승세'); // 초기값은 상승세로 설정

  useEffect(() => {
    const fetchDummyData = () => {
      // 더미 가격 및 거래량 생성
      const dummyPrice = (Math.random() * (60000 - 50000) + 50000).toFixed(2);
      const dummyVolume = (Math.random() * 100).toFixed(2);
      const currentTime = new Date().toLocaleTimeString();

      setPriceData((prevPriceData) => [...prevPriceData, dummyPrice]);
      setTimeData((prevTimeData) => [...prevTimeData, currentTime]);
      setVolumeData((prevVolumeData) => [...prevVolumeData, dummyVolume]);

      // 최근 5개의 가격을 기반으로 트렌드 분석
      const recentPrices = [...priceData, dummyPrice].slice(-5);  // 최근 5개 데이터
      const priceDifference = recentPrices[recentPrices.length - 1] - recentPrices[0];
      setTrend(priceDifference > 0 ? '상승세' : '하락세');

      // 가격이 59000 이상이면 알림 추가
      if (dummyPrice > 59000) {
        setAlerts((prevAlerts) => [...prevAlerts, `High Price Alert: $${dummyPrice} at ${currentTime}`]);
      }

      // 더미 거래 기록 생성
      const dummyTrade = {
        price: dummyPrice,
        volume: dummyVolume,
        time: currentTime,
      };
      setTradeHistory((prevHistory) => {
        const updatedHistory = [...prevHistory, dummyTrade];
        return updatedHistory.length > 15 ? updatedHistory.slice(-15) : updatedHistory;
      });
    };

    fetchDummyData();
    const interval = setInterval(fetchDummyData, 2000);
    return () => clearInterval(interval);
  }, [priceData]);

  const priceChartData = {
    labels: timeData,
    datasets: [
      {
        label: 'Bitcoin Price',
        data: priceData,
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  };

  const volumeChartData = {
    labels: timeData,
    datasets: [
      {
        label: 'Bitcoin Volume',
        data: volumeData,
        backgroundColor: 'green',
      },
    ],
  };

  return (
    <div>
      <h1>Bitcoin Tracker with Dummy Data</h1>

      {/* Flexbox로 요소를 가로로 나란히 배치 및 반응형 설정 */}
      <div style={styles.container}>

        {/* 비트코인 트래커와 거래량 차트 (세로로 나란히 배치) */}
        <div style={styles.chartSection}>
          <h2>Bitcoin Price</h2>
          <Line data={priceChartData} />

          <h2>Bitcoin Volume</h2>
          <Bar data={volumeChartData} />
        </div>

        {/* 거래 기록 테이블 */}
        <div style={styles.tableContainer}>
          <h2>Trade History (Dummy) - Latest 15</h2>
          <table border="1" cellPadding="10" cellSpacing="0">
            <thead>
              <tr>
                <th>Time</th>
                <th>Price (USD)</th>
                <th>Volume</th>
              </tr>
            </thead>
            <tbody>
              {tradeHistory.map((trade, index) => (
                <tr key={index}>
                  <td>{trade.time}</td>
                  <td>{trade.price}</td>
                  <td>{trade.volume}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* 비트코인 트렌드 분석 */}
      <div style={styles.trendContainer}>
        <h2>Current Trend: {trend}</h2>
        <p>최근 5개의 가격을 기반으로 분석된 결과: {trend}</p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row', // 기본적으로 가로로 배치
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    flexWrap: 'wrap', // 화면 크기가 작아지면 세로로 배치
  },
  chartSection: {
    flex: 1,
    minWidth: '300px',
    marginRight: '20px',
    marginBottom: '20px',
  },
  tableContainer: {
    flex: 1,
    minWidth: '300px',
  },
  trendContainer: {
    marginTop: '20px',
    padding: '10px',
    backgroundColor: '#f0f0f0',
    borderRadius: '8px',
  },
};

export default App;
