import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [priceData, setPriceData] = useState([]);
  const [timeData, setTimeData] = useState([]);
  const [alerts, setAlerts] = useState([]);
  const [tradeHistory, setTradeHistory] = useState([]);

  useEffect(() => {
    const fetchDummyData = () => {
      // 더미 가격 생성
      const dummyPrice = (Math.random() * (60000 - 50000) + 50000).toFixed(2);
      const currentTime = new Date().toLocaleTimeString();

      setPriceData((prevPriceData) => [...prevPriceData, dummyPrice]);
      setTimeData((prevTimeData) => [...prevTimeData, currentTime]);

      // 가격이 59000 이상이면 알림 추가
      if (dummyPrice > 59000) {
        setAlerts((prevAlerts) => [...prevAlerts, `High Price Alert: $${dummyPrice} at ${currentTime}`]);
      }

      // 더미 거래 기록 생성
      const dummyTrade = {
        price: dummyPrice,
        volume: (Math.random() * 5).toFixed(2),
        time: currentTime,
      };
      setTradeHistory((prevHistory) => {
        const updatedHistory = [...prevHistory, dummyTrade];
        return updatedHistory.length > 15 ? updatedHistory.slice(-15) : updatedHistory;
      });
    };

    fetchDummyData();
    const interval = setInterval(fetchDummyData, 2000);
    return () => clearInterval(interval);
  }, []);

  const chartData = {
    labels: timeData,
    datasets: [
      {
        label: 'Bitcoin Price',
        data: priceData,
        fill: false,
        borderColor: 'blue',
        tension: 0.1,
      },
    ],
  };

  return (
    <div>
      <h1>Bitcoin Tracker with Dummy Data</h1>

      {/* Flexbox로 요소를 가로로 나란히 배치 */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>

        {/* 비트코인 트래커 */}
        <div style={{ flex: 1, marginRight: '20px' }}>
          <Line data={chartData} />
        </div>

        {/* 거래 기록 테이블 */}
        <div style={{ flex: 1 }}>
          <h2>Trade History (Dummy) - Latest 15</h2>
          <table border="1" cellPadding="10" cellSpacing="0">
            <thead>
              <tr>
                <th>Time</th>
                <th>Price (USD)</th>
                <th>Volume</th>
              </tr>
            </thead>
            <tbody>
              {tradeHistory.map((trade, index) => (
                <tr key={index}>
                  <td>{trade.time}</td>
                  <td>{trade.price}</td>
                  <td>{trade.volume}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default App;
